import SwiftUI

struct ContentView: View {
    @StateObject private var noteViewModel = NoteViewModel()
    @State private var showingAddNote = false
    @State private var searchText = ""

    var body: some View {
        NavigationStack {
            ZStack {
                LinearGradient(colors: [.purple.opacity(0.3), .blue.opacity(0.2)],
                             startPoint: .topLeading,
                             endPoint: .bottomTrailing)
                    .ignoresSafeArea()

                VStack {
                    if noteViewModel.filteredNotes.isEmpty {
                        EmptyStateView()
                    } else {
                        notesList
                    }
                }
            }
            .navigationTitle("Note Space")
            .searchable(text: $searchText)
            .onChange(of: searchText) { _ in
                noteViewModel.filterNotes(searchText)
            }
            .toolbar {
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button(action: { showingAddNote = true }) {
                        Image(systemName: "plus.circle.fill")
                            .font(.title2)
                            .foregroundStyle(.purple)
                    }
                }
            }
            .sheet(isPresented: $showingAddNote) {
                AddNoteView(noteViewModel: noteViewModel)
            }
        }
    }

    private var notesList: some View {
        ScrollView {
            LazyVGrid(columns: [GridItem(.flexible()), GridItem(.flexible())], spacing: 16) {
                ForEach(noteViewModel.filteredNotes) { note in
                    NoteCard(note: note, noteViewModel: noteViewModel)
                        .transition(.scale)
                }
            }
            .padding()
        }
    }
}

struct EmptyStateView: View {
    var body: some View {
        VStack(spacing: 20) {
            Image(systemName: "note.text")
                .font(.system(size: 70))
                .foregroundStyle(.purple.opacity(0.7))

            Text("No Notes Yet")
                .font(.title2)
                .fontWeight(.bold)

            Text("Start creating your notes by tapping the + button")
                .multilineTextAlignment(.center)
                .foregroundStyle(.secondary)
        }
        .padding()
    }
}
