                if allTags.isEmpty {
                    EmptyTagsView()
                } else {
                    ScrollView {
                        LazyVStack(spacing: 16) {
                            ForEach(allTags, id: \.self) { tag in
                                TagSection(tag: tag, notes: taggedNotes[tag] ?? [])
                            }
                        }
                        .padding()
                    }
                }
            }
            .navigationTitle("Tags")
        }
    }
}

