```swift
import SwiftUI

struct EditNoteView: View {
    @Environment(\.dismiss) private var dismiss
    let note: Note
    @ObservedObject var noteViewModel: NoteViewModel

    @State private var title: String
    @State private var content: String
    @State private var tags: String
    @State private var reminder: Date
    @State private var hasReminder: Bool

    init(note: Note, noteViewModel: NoteViewModel) {
        self.note = note
        self.noteViewModel = noteViewModel
        _title = State(initialValue: note.title)
        _content = State(initialValue: note.content)
        _tags = State(initialValue: note.tags.joined(separator: ", "))
        _reminder = State(initialValue: note.reminder ?? Date())
        _hasReminder = State(initialValue: note.reminder != nil)
    }

    var body: some View {
        NavigationStack {
            Form {
                Section(header: Text("Note Details")) {
                    TextField("Title", text: $title)
                    TextEditor(text: $content)
                        .frame(height: 150)
                    TextField("Tags (comma separated)", text: $tags)
                }

                Section(header: Text("Reminder")) {
                    Toggle("Set Reminder", isOn: $hasReminder)

                    if hasReminder {
                        DatePicker("Reminder Time",
                                 selection: $reminder,
                                 in: Date()...)
                    }
                }
            }
            .navigationTitle("Edit Note")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .cancellationAction) {
                    Button("Cancel") {
                        dismiss()
                    }
                }

                ToolbarItem(placement: .confirmationAction) {
                    Button("Save") {
                        saveNote()
                    }
                    .disabled(title.isEmpty || content.isEmpty)
                }
            }
        }
    }

    private func saveNote() {
        let tagArray = tags
            .split(separator: ",")
            .map { String($0).trimmingCharacters(in: .whitespaces) }
            .filter { !$0.isEmpty }

        let updatedNote = Note(
            id: note.id,
            title: title,
            content: content,
            tags: tagArray,
            reminder: hasReminder ? reminder : nil
        )

        noteViewModel.updateNote(updatedNote)
        dismiss()
    }
}
